mapper.update:
 
UPDATE G2_SELLER 
   SET SELLER_SHOP_NAME = #{sellerShopName},
       STATUS = #{status}
 WHERE SELLER_ID = #{sellerId}

mapper.select:
SELECT RESTO_TOTAL_PRICE, RESTO_TOTAL_COUNT, RESTO_NAME, FILE_NAME, R_SEQ, RESTO_DEPOSIT, RESERV_PERSON FROM ( SELECT ROWNUM AS RNM, RESTO_TOTAL_PRICE, RESTO_TOTAL_COUNT, RESTO_NAME, FILE_NAME, R_SEQ, RESTO_DEPOSIT, RESERV_PERSON FROM ( SELECT (RESTO_DEPOSIT * RESERV_PERSON) AS RESTO_TOTAL_PRICE, RESTO_TOTAL_COUNT, RESTO_NAME, FILE_NAME, R_SEQ, RESTO_DEPOSIT, RESERV_PERSON FROM ( SELECT COUNT(ORDER_SEQ) AS RESTO_TOTAL_COUNT, R.RESTO_NAME AS RESTO_NAME, F.FILE_NAME AS FILE_NAME, R.R_SEQ AS R_SEQ, R.RESTO_DEPOSIT AS RESTO_DEPOSIT, SUM(NVL(O.RESERV_PERSON, 0)) AS RESERV_PERSON FROM ORDER_LIST O, RESTO_INFO R, RESTO_FILE F WHERE O.R_SEQ(+) = R.R_SEQ AND O.STATUS(+) = 'Y' AND F.R_SEQ = R.R_SEQ AND F.FILE_SEQ = 1
AND RESTO_NAME LIKE '%' || #{searchValue} || '%'
GROUP BY R.RESTO_NAME, F.FILE_NAME, R.R_SEQ, RESTO_DEPOSIT ) ORDER BY (RESTO_DEPOSIT * RESERV_PERSON) DESC ) ) WHERE RNM BETWEEN #{startRow} AND #{endRow}

SELECT GIFT_TOTAL_CNT, ORDER_TOTAL_CNT, GIFT_TOTAL_PRICE, P_NAME, PRODUCT_SEQ, P_PRICE, FILE_NAME FROM ( SELECT ROWNUM AS RNM, ORDER_TOTAL_CNT, GIFT_TOTAL_CNT, GIFT_TOTAL_PRICE, P_NAME, PRODUCT_SEQ, P_PRICE, FILE_NAME FROM ( SELECT COUNT(O.ORDER_SEQ) AS ORDER_TOTAL_CNT, SUM(NVL(TOTAL_CNT, 0)) AS GIFT_TOTAL_CNT, SUM(NVL(TOTAL_PRICE, 0)) AS GIFT_TOTAL_PRICE, P.P_NAME, P.PRODUCT_SEQ, P.P_PRICE, NVL(F.FILE_NAME, 'gift.png') AS FILE_NAME FROM ORDER_LIST O, PRODUCT_INFO P, PAY_LIST PL, PRODUCT_FILE F WHERE O.PRODUCT_SEQ(+) = P.PRODUCT_SEQ AND O.ORDER_SEQ = PL.ORDER_ID(+) AND O.STATUS(+) = 'Y' AND F.PRODUCT_SEQ(+) = P.PRODUCT_SEQ AND F.FILE_SEQ(+) = 1
AND P_NAME LIKE '%' || #{searchValue} || '%'
GROUP BY P.P_NAME, P.PRODUCT_SEQ, P.P_PRICE, F.FILE_NAME ORDER BY GIFT_TOTAL_PRICE DESC ) ) WHERE RNM BETWEEN #{startRow} AND #{endRow}

