    		RESTO_CONTENT = #{changeText}
		
	
WHERE
    R_SEQ = #{rSeq}

UPDATE 
    MENU
SET
	
		
    		MENU_NAME = #{changeText}
		
		
    		MENU_CONTENT = #{changeText}
		
	
WHERE
    MENU_SEQ = #{menuSeq}

UPDATE 
    RESTO_INFO
SET
    STATUS = #{status}
WHERE
    R_SEQ = #{rSeq}

UPDATE 
    PRODUCT_INFO
SET
    STATUS = #{status}
WHERE
    PRODUCT_SEQ = #{productSeq}

UPDATE 
    PRODUCT_INFO
SET
	
		
    		P_NAME = #{changeText}
		
		
    		P_CONTENT = #{changeText}
		
	
WHERE
    PRODUCT_SEQ = #{productSeq}
