mapper.insert:
SELECT NVL(MAX(FILE_SEQ), 0) + 1 FROM PRODUCT_FILE WHERE PRODUCT_SEQ = #{productSeq}

INSERT INTO PRODUCT_FILE 
(
   PRODUCT_SEQ,
   FILE_SEQ,
   FILE_NAME
) 
VALUES 
(
    #{productSeq},
    #{fileSeq},
    #{fileName}
)

mapper.select:
SELECT PRODUCT_SEQ, P_NAME, SELLER_ID, P_PRICE, P_CONTENT, REG_DATE, STATUS, PRODUCT_CATEGORY, FILE_NAME, FILE_SEQ FROM (SELECT ROWNUM AS RNUM, PRODUCT_SEQ, P_NAME, SELLER_ID, P_PRICE, P_CONTENT, REG_DATE, STATUS, PRODUCT_CATEGORY, FILE_NAME, FILE_SEQ FROM (SELECT A.PRODUCT_SEQ, NVL(A.P_NAME,'') AS P_NAME, NVL(A.SELLER_ID,'') AS SELLER_ID, NVL(A.P_PRICE,'') AS P_PRICE, NVL(A.P_CONTENT,'') AS P_CONTENT, TO_CHAR(A.REG_DATE, 'YYYY.MM.DD HH:MM:SS') AS REG_DATE, NVL(A.STATUS,'') AS STATUS, NVL(A.PRODUCT_CATEGORY,'') AS PRODUCT_CATEGORY, NVL(B.FILE_NAME,'') AS FILE_NAME, NVL(B.FILE_SEQ,1) AS FILE_SEQ FROM PRODUCT_INFO A, PRODUCT_FILE B WHERE B.FILE_SEQ = 1 AND A.PRODUCT_SEQ = B.PRODUCT_SEQ
AND A.PRODUCT_CATEGORY = #{productType}
AND A.P_NAME LIKE '%'||#{searchValue}||'%'
AND A.P_CONTENT LIKE '%'||#{searchValue}||'%'
ORDER BY A.REG_DATE DESC
ORDER BY TO_NUMBER(A.P_PRICE) DESC
ORDER BY TO_NUMBER(A.P_PRICE) ASC
ORDER BY A.REG_DATE ASC
)) WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.delete:
DELETE FROM 
    FAVORITE_LIST
WHERE
    USER_ID = #{userId}
    AND PRODUCT_SEQ = #{productSeq}
